//const {currentUser, apiUrl, mockData, simDataLoadingTime} = config
import {createContext, useEffect, useState} from "react";
import {USER_MAIN_DATA as usersMocked, USER_ACTIVITY, USER_AVERAGE_SESSIONS, USER_PERFORMANCE } from '../src/dataMocked'

const mockData = true;
let currentUser = 12;
/**
 * Context
 */
export const DashboardContext = createContext()
function DashboardContextProvider({children}) {
    /**
     * State
     */
    const [user, setUser] = useState(12)
    const [activity, setActivity] = useState()
    const [averageSessions, setAverageSessions] = useState()
    const [performance, setPerformance] = useState()
    const [performanceKind, setPerformanceKind] = useState()
    const [apiError, setApiError] = useState(false)
    /**
     * Get user data
     */
    const getUser = () => {
        if (!mockData) fetch(`${apiUrl}/user/${currentUser}`)
            .then((response => response.json()))
            .then(data => setUser(data.data))
            .catch((error) => setApiError(true))
        else setUser(usersMocked.find((item) => item.id === currentUser))
    }

    useEffect(() => {
        getUser()
    }, [])
}
export default DashboardContext
