import {RadarChart, Legend, PolarAngleAxis, PolarGrid, Radar, PolarRadiusAxis} from "recharts";

function Performance(){
    const data = [
        {
            value: 80,
            kind: 1
        },
        {
            value: 120,
            kind: 2
        },
        {
            value: 140,
            kind: 3
        },
        {
            value: 50,
            kind: 4
        },
        {
            value: 200,
            kind: 5
        },
        {
            value: 90,
            kind: 6
        }
    ]
    // Rotate radar to start with intensity
    const angleStart = 210

    // Format kind
    const titleKind = {
        cardio: 'Cardio',
        energy: 'Energie',
        endurance: 'Endurance',
        strength: 'Force',
        speed: 'Vitesse',
        intensity: 'Intensité',
    }

    const formatKind = (id) => titleKind[data.find(item => item.kind[id])]
    return (
        <>
            <RadarChart cx="50%"
                        cy="50%"
                        outerRadius="65%"
                        startAngle={angleStart}
                        endAngle={angleStart + 360}
                        width={250}
                        height={260}
                        data={data}
            >
                <PolarGrid />
                <PolarAngleAxis dataKey={formatKind}
                                tick={{ fill: 'white', fontSize: '0.875em'}}
                />
                <Radar legendType="none"
                       dataKey="value"
                       stroke="#E60000"
                       fill="#E60000"
                       fillOpacity={0.7}
                />
                <Legend />
            </RadarChart>

        </>
    )
}
export default Performance;
