import {createContext, useEffect, useState} from "react"
import { useParams } from "react-router-dom"
import {usersMocked, activitiesMocked, averageSessionsMocked, performanceMocked} from '../src/dataMocked'
/**
 * Context
 */
export const DashboardContext = createContext()

 function Service({children}) {

    /**
     * State
     */
    const [user, setUser] = useState()
    const [activity, setActivity] = useState()
    const [averageSessions, setAverageSessions] = useState()
    const [performance, setPerformance] = useState()
    const [performanceKind, setPerformanceKind] = useState()
    const [apiError, setApiError] = useState(false)
    const params = useParams()
    console.log(params.id)
    /**
     * Get user data
     */
    const getUser = () => {
        if (process.env.REACT_APP_USE_API) fetch(`http://localhost:3000/user/${params.id}`)
            .then((response => response.json()))
            .then(data => {
                setUser(data.data)
            })
            .catch((error) => setApiError(true))
        else setUser(usersMocked.find((item) => item.id === params.id))
    }

    useEffect(() => {
         getUser()
    }, [])

    /**
     * Get user activity data
     */
    const getUserActivity = () => {
        if (process.env.REACT_APP_USE_API) fetch(`http://localhost:3000/user/${params.id}/activity`)
            .then((response => response.json()))
            .then(data => setActivity(data.data.sessions))
            .catch((error) => setApiError(true))
        else setActivity(activitiesMocked.find((activity) => activity.userId === params.id).sessions)
    }

    /**
     * Get user average sessions data
     */
    const getUserAverageSessions = () => {
        if (process.env.REACT_APP_USE_API) fetch(`http://localhost:3000/user/${params.id}/average-sessions`)
            .then((response => response.json()))
            .then(data => setAverageSessions(data.data.sessions))
            .catch((error) => setApiError(true))
        else setAverageSessions(averageSessionsMocked.find((averageSessions) => averageSessions.userId === params.id).sessions)
    }

    /**
     * Get user performance data
     */
    const getUserPerformance = () => {
        if (process.env.REACT_APP_USE_API) fetch(`http://localhost:3000/user/${params.id}/performance`)
            .then((response => response.json()))
            .then(data => {
                setPerformance(data.data.data)
                setPerformanceKind(data.data.kind)
            })
            .catch((error) => setApiError(true))
        else {
            const performance = performanceMocked.find((performance) => performance.userId === params.id)
            setPerformance(performance.data)
            setPerformanceKind(performance.kind)
        }
    }

    useEffect(() => {
            getUserActivity()
            getUserAverageSessions()
            getUserPerformance()
    }, [])

    return (

        <>
            { console.log(user, activity, averageSessions, performance, performanceKind, apiError)}


            <p>{apiError}</p>

        </>
    )
}
export default Service;
