import { Bar, CartesianGrid, Legend, ResponsiveContainer, Tooltip, XAxis, YAxis} from "recharts";
//import data from '../dataMocked'
import '../styles/Activity.css'
import {BarChart} from "recharts";

function Activity(){
    const formatDay = (item) => (new Date(item)).getDate()
    const formatLegend = (value) => value === 'kilogram' ? 'Poids (kg)' : 'Calories brûlées (kCal)'
    const data1 = [
        {
            name: 'Page A',
            uv: 4000,
            pv: 2400,
            amt: 2400,
        },
        {
            name: 'Page B',
            uv: 3000,
            pv: 1398,
            amt: 2210,
        },
        {
            name: 'Page C',
            uv: 2000,
            pv: 9800,
            amt: 2290,
        },
        {
            name: 'Page D',
            uv: 2780,
            pv: 3908,
            amt: 2000,
        },
        {
            name: 'Page E',
            uv: 1890,
            pv: 4800,
            amt: 2181,
        },
        {
            name: 'Page F',
            uv: 2390,
            pv: 3800,
            amt: 2500,
        },
        {
            name: 'Page G',
            uv: 3490,
            pv: 4300,
            amt: 2100,
        },
    ];
    const data = [
        {
            day: '2020-07-01',
            kilogram: 80,
            calories: 240
        },
        {
            day: '2020-07-02',
            kilogram: 80,
            calories: 220
        },
        {
            day: '2020-07-03',
            kilogram: 81,
            calories: 280
        },
        {
            day: '2020-07-04',
            kilogram: 81,
            calories: 290
        },
        {
            day: '2020-07-05',
            kilogram: 80,
            calories: 160
        },
        {
            day: '2020-07-06',
            kilogram: 78,
            calories: 162
        },
        {
            day: '2020-07-07',
            kilogram: 76,
            calories: 390
        }
    ]
    return(
        <>
            <h2 className="chart-title">Activité quotidienne</h2>
            <ul className="chart-legend">
                <li>Poids (kg)</li>
                <li>Calories brûlées (kCal)</li>
            </ul>


            <BarChart width={1000} height={300}  data={data}>
                <CartesianGrid strokeDasharray="2" vertical={false}/>
                <Legend
                    verticalAlign="top"
                    align="right"
                    iconSize={8}
                    iconType="circle"
                    height={100}
                    formatter={formatLegend}
                />
                <XAxis
                    dataKey="day"
                    tickMargin={16}
                    tickFormatter={formatDay}
                    tickSize={0}
                    minTickGap={30}
                />
                <YAxis
                    yAxisId="1"
                    dataKey="kilogram"
                    orientation='right'
                    tickMargin={40}
                    tickSize={0}
                    axisLine={false}
                    domain={['dataMin-5', 'dataMax+0']}
                    interval={'preserveEnd'}
                    tickCount={3}
                />
                <YAxis
                    yAxisId="2"
                    dataKey="calories"
                    orientation='left'
                    tickCount={5}
                    domain={['dataMin-100', 'dataMax+0']}
                    hide
                />

                <Tooltip />
                <Bar
                    yAxisId="1"
                    dataKey="kilogram"
                    fill="#282D30"
                    barSize={7}
                    radius={[3, 3, 0, 0]}
                />
                <Bar
                    yAxisId="2"
                    dataKey="calories"
                    fill="#E60000"
                    barSize={7}
                    radius={[3, 3, 0, 0]}
                />
            </BarChart>



                <BarChart
                    width={500}
                    height={300}
                    data={data1}
                    margin={{
                        top: 20,
                        right: 30,
                        left: 20,
                        bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" />
                    <YAxis yAxisId="left" orientation="left" stroke="#8884d8" />
                    <YAxis yAxisId="right" orientation="right" stroke="#82ca9d" />
                    <Tooltip />
                    <Legend />
                    <Bar yAxisId="left" dataKey="pv" fill="#8884d8" />
                    <Bar yAxisId="right" dataKey="uv" fill="#82ca9d" />
                </BarChart>

            {/*
                <BarChart width={800} height={300} data={data}>
                    <CartesianGrid strokeDasharray="2" vertical={false} />
                    <Legend
                        verticalAlign="top"
                        align="right"
                        iconSize={8}
                        iconType="circle"
                        height={100}
                        formatter={formatLegend}
                    />
                    <XAxis dataKey="day" tickMargin={16} tickFormatter={formatDay} tickSize={0} minTickGap={30} />
                    <YAxis dataKey="kilogram" orientation='right' tickMargin={40} tickSize={0} axisLine={false} domain={['dataMin-5', 'dataMax+0']} interval={'preserveEnd'} tickCount={3}/>
                    <YAxis
                        dataKey="calories"
                        hide
                        orientation='right'
                        domain={['dataMin-100', 'dataMax+0']}
                    />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="kilogram" fill="#282D30"  barSize={10} radius={[5, 5, 0, 0]}/>
                    <Bar dataKey="calories" fill="#E60000"  barSize={10} radius={[5, 5, 0, 0]}/>
                </BarChart>
            */}
        </>
    )
}
export default Activity;
