import data from '../dataMocked'
import Activity from "../components/Activity";
import '../styles/Home.css'
import AverageSessions from "../components/AverageSessions";
import Performance from "../components/Performance";
import Score from "../components/Score";
import Stat from "../components/Stat";
import {useContext, useEffect, useState} from "react";
import {DashboardContext, getUser, getUserActivity, getUserAverageSessions, getUserPerformance} from "../Service";
import {useParams} from "react-router-dom";


function Home(){
    const params = useParams()
    let user, activity, sessions, performance;
    const [userApi, setUserApi] = useState()
    const [activityApi, setActivityApi] = useState()
    const [averageSessionsApi, setAverageSessionsApi] = useState()
    const [performanceApi, setPerformanceApi] = useState()
    const [apiError, setApiError] = useState(false)
    let userMock = data.USER_MAIN_DATA.find(x => x.id === 12)
    let activityMock = data.USER_ACTIVITY.find(x => x.userId === userMock.id)
    let sessionsMock = data.USER_AVERAGE_SESSIONS.find(x => x.userId === userMock.id)
    let performanceMock = data.USER_PERFORMANCE.find(x => x.userId === userMock.id)
    if(process.env.REACT_APP_USE_API){
        params.id =12
    }
    useEffect(() => {
        if(!process.env.REACT_APP_USE_API){
            user = userMock
            activity = activityMock;
            sessions = sessionsMock;
            performance = performanceMock
        }else {
            getUser(params.id).then(setUserApi)
            getUserActivity(params.id).then(setActivityApi)
            getUserAverageSessions(params.id).then(setAverageSessionsApi)
            getUserPerformance(params.id).then(setPerformanceApi)
            user = userApi;
            activity = activityApi;
            sessions = averageSessionsApi;
            performance = performanceApi
        }
    }, [params.id])
    console.log(userApi)
    console.log(activityApi)
    console.log(averageSessionsApi)
    console.log(performanceApi)
    /*if(process.env.REACT_APP_USE_API){
        user = userApi;
        activity = activityApi;
        sessions = averageSessionsApi;
        performance = performanceApi
    }else{
        user = userMock
        activity = activityMock;
        sessions = sessionsMock;
        performance = performanceMock
    }*/
    console.log(user)


    return(
            <section className="homepage">
                        <header className="section-header">
                            <h1 className="section-title">Bonjour {user && user.userInfos.firstName}</h1>
                            <p className="section-secondary">F√©licitation ! Vous avez explos√© vos objectifs hier üëè</p>
                        </header>
                        <div className="homepage-grid">
                            <div className="chart-activity-stats">
                                <div className="chart-activity">
                                    {activity && <Activity data={activity.sessions}/>}
                                </div>
                                <div className="chart-sessions-performance-score">
                                    <div className="chart-average-sessions">
                                        {sessions && <AverageSessions data={sessions.sessions}/>}
                                    </div>
                                    <div className="chart-performance">
                                        {performance && <Performance data={performance.data}  kind={performance.kind}/>}
                                    </div>
                                    <div className="chart-score">
                                        {user && <Score data={user.score||user.todayScore}/>}
                                    </div>
                                </div>
                            </div>
                            <div className="stats">
                                {user && <Stat data={user.keyData}/>}
                            </div>
                        </div>
            </section>

    )
}
export default Home;
