import {BarChart, Bar, CartesianGrid, Legend, ResponsiveContainer, Tooltip, XAxis, YAxis} from "recharts";
import data from '../dataMocked'


function Activity(){
    const d = [
        {
            day: '2020-07-01',
            kilogram: 80,
            calories: 240
        },
        {
            day: '2020-07-02',
            kilogram: 80,
            calories: 220
        },
        {
            day: '2020-07-03',
            kilogram: 81,
            calories: 280
        },
        {
            day: '2020-07-04',
            kilogram: 81,
            calories: 290
        },
        {
            day: '2020-07-05',
            kilogram: 80,
            calories: 160
        },
        {
            day: '2020-07-06',
            kilogram: 78,
            calories: 162
        },
        {
            day: '2020-07-07',
            kilogram: 76,
            calories: 390
        }
    ]
    return(
        <>
            <h2 className="chart-title">Activité quotidienne</h2>
            <ul className="chart-legend">
                <li>Poids (kg)</li>
                <li>Calories brûlées (kCal)</li>
            </ul>
            <ResponsiveContainer width="100%" height="100%">
                <BarChart  data={d}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis
                        dataKey="day"
                        tickMargin={16}
                        tickFormatter={formatDay}
                        tickSize={0}
                        minTickGap={30}
                    />
                    <YAxis
                        yAxisId="kilogram"
                        orientation='right'
                        tickMargin={40}
                        tickSize={0}
                        axisLine={false}
                        domain={['dataMin-5', 'dataMax+0']}
                        interval={'preserveEnd'}
                        tickCount={3}
                    />
                    <YAxis
                        yAxisId="calories"
                        hide
                        orientation='right'
                        domain={['dataMin-100', 'dataMax+0']}
                    />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="kilogram" fill="#8884d8" />
                    <Bar dataKey="calories" fill="#82ca9d" />
                </BarChart>
            </ResponsiveContainer>
        </>
    )
}
export default Activity;
